import React, { Component } from 'react'
import Carousel from 'react-bootstrap/Carousel'
import './testimonial.css'
import styled from 'styled-components'

const Image = styled.div`
  background-image: url(${props => props.image});
  box-shadow: 0px 100vh rgba(0, 0, 0, ${props => props.x} ) inset;
  position: relative;
  height: ${props => props.height};
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  display: flex;
  flex-direction: column;
  align-items: ${props => props.align};
  justify-content: ${props => props.justify};
  width: 100%;
  filter:  contrast(${props => props.contrast});
  filter: brightness(${props => props.brightness});
`;


class Testimonial extends Component {
  render() {
    return (
      <Image x={this.props.x} height={this.props.height} image={this.props.image} brightness="100%" contrast="140%" align='center' justify='center'>
        <h1 className='head2'>Testimonials</h1>
