import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components'
import { FaCaretDown,FaCaretUp } from 'react-icons/fa';
import './header.css'
import logo1 from './riees1.png'
import logo2 from './riees3.png'

const StyledLink = styled(Link)`
  text-transform: uppercase;
  box-sizing: 100%;
  color:#505050;
  font-size: calc(6.5px + 1vw);
  letter-spacing: 3px;
  padding: 1% 0;
  text-decoration: none;
  text-align: center;
  padding:0 0 0 3px;
  transition:  all 0.15s linear ;
  border-bottom: 3px solid transparent;
  :hover {
    border-bottom: 3px solid transparent;
    color: #000066;
    transform: scale(1.05);
    transition:  all 0.15s linear;
  }
  ${props => props.active &&`
    border-bottom: 3px solid #000066;
    color: #000066;
    transition:  all 0.15s linear;
    cursor:text;
    :hover {
    border-bottom: 3px solid #000066;
    color: #000066;
    transform: scale(1);
    transition:  all 0.15s linear;
  }
  `}
  ${props => props.li &&`
  font-size: calc(1.5px + 1vw);
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  }
  `}
`;



class Header extends Component {
  constructor() {
    super();
    this.state = {
       scroll: false,
       active: 'Home'
    }
  }
  handleScroll() { 
   if (document.documentElement.scrollTop > 0) {
      this.setState({
        scroll: true
      })
    }
    if (document.documentElement.scrollTop === 0) {
      this.setState({
        scroll: false
      })
    }
  }
  handleClick(active){
    this.setState({
      active: active
    })
  }
  componentDidMount() {
    window.onscroll = () => this.handleScroll()
  }
  render() {
    return (
     <div className={this.state.scroll ? 'container2': 'container'}>    
        <StyledLink onClick={()=> this.handleClick('Home')} active={this.state.active === 'Home' ? true : false} className={this.state.scroll ? 'scroll': ''} to={"/Home"} >Home</StyledLink>
        <StyledLink onClick={()=> this.handleClick('Institute')} active={this.state.active === 'Institute' ? true : false} className={this.state.scroll ? 'scroll': ''} to={"/Institutes"} >Our Institutes</StyledLink>
        <div className='div'>
          <img className={this.state.scroll ? 'logo1Scroll': 'logo1'} src={logo1} alt=''/>
          <img className={this.state.scroll ? 'logo2Scroll': 'logo2'} src={logo2} alt=''/>
        </div> 
        <StyledLink onClick={()=> this.handleClick('Es')} active={this.state.active === 'Es' ? true : false} className={this.state.scroll ? 'dropscroll': 'dropdown'} to={"/Es"} >
        Espirito Santo
        <FaCaretDown/>
        <div class="dropdown-content">
        <StyledLink li onClick={()=> this.handleClick('Institute')} active={this.state.active === 'Institute' ? true : false} className={this.state.scroll ? 'scroll2': ''} to={"/Institutes"} >Coming to Espirito Santo</StyledLink>
        <StyledLink li onClick={()=> this.handleClick('Institute')} active={this.state.active === 'Institute' ? true : false} className={this.state.scroll ? 'scroll2': ''} to={"/Institutes"} >Living in Espirito Santo</StyledLink>
        <StyledLink li onClick={()=> this.handleClick('Institute')} active={this.state.active === 'Institute' ? true : false} className={this.state.scroll ? 'scroll2': ''} to={"/Institutes"} >Our Cities</StyledLink>
        </div>
        </StyledLink>
        <StyledLink onClick={()=> this.handleClick('About')} active={this.state.active === 'About' ? true : false} className={this.state.scroll ? 'scroll': ''} to={"/About"} >About Us</StyledLink>
     </div>
    )
  }
}
export default Header
